function pNotifyMessage(e){new PNotify({title:"error"===e.level?"Error.":"Info.",text:e.message,type:e.level,animation:"slide",animate_speed:"slow",hide:"true",shadow:"true",delay:3e3,mouse_reset:!0})}$(function(){PNotify.prototype.options.styling="fontawesome";var e=$("input[name=notifications]").map(function(){return $(this).val()}).get();for(var t in e)new pNotifyMessage(jQuery.parseJSON(e[t]))});